@Composable
fun VanSidebar(
    selectedIndex: Int,
    onChange: (Int) -> Unit,
    modifier: Modifier = Modifier,
    content: @Composable VanSidebarScope.() -> Unit
) {
    Column(modifier = modifier.background(Color.White)) {
        val scope = VanSidebarScopeImpl(selectedIndex, onChange)
        scope.content()
    }
}

interface VanSidebarScope {
    @Composable
    fun VanSideBarItem(title: String)
}

private class VanSidebarScopeImpl(
    private val selectedIndex: Int,
    private val onSelect: (Int) -> Unit,
) : VanSidebarScope {

    private var index = 0
    @Composable
    override fun VanSideBarItem(title: String) {
        val currentIndex = index++
        key(currentIndex+selectedIndex) {
            Log.e("DONG", "ğŸš€ ~ VanSideBarItem ~ recomposition currentIndex+selectedIndex: ${currentIndex+selectedIndex}")
            val isSelected = selectedIndex == currentIndex

            val backgroundColor by animateColorAsState(
                if (isSelected) Color.LightGray else Color.Transparent,
                label = "SidebarItemBg"
            )

            Box(
                modifier = Modifier
                    .width(90.dp)
                    .clickable { onSelect(currentIndex) }
                    .background(backgroundColor)
                    .padding(16.dp)
            ) {
                Text(
                    text = title,
                    color = if (isSelected) Color.Blue else Color.Black
                )
            }
        }
    }
}
